func TestSubString(t *testing.T) {
	str := `{"alipay_trade_pay_response":{"code":"10000","msg":"Success","buyer_logon_id":"854***@qq.com","buyer_pay_amount":"0.01","buyer_user_id":"2088102363632794","fund_bill_list":[{"amount":"0.01","fund_channel":"PCREDIT"}],"gmt_payment":"2019-08-29 20:22:02","invoice_amount":"0.01","out_trade_no":"GZ201901301040361013","point_amount":"0.00","receipt_amount":"0.01","total_amount":"0.01","trade_no":"2019082922001432790585666965"},"sign":""}`

	index := strings.Index(str, `":`)
	fmt.Println("index:", index)
	indexEnd := strings.Index(str, `,"sign"`)
	fmt.Println("indexEnd:", indexEnd)

	fmt.Println("sub:", str[index+2:indexEnd])
}

